    private void puntosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_puntosActionPerformed
        if (puntos.getSelectedIndex() == 0) {
            this.setVisible(false);
            Acee aux = new Acee();
            aux.setVisible(true);
        }
        if (puntos.getSelectedIndex() == 1) {
            this.setVisible(false);
            Errorrival aux = new Errorrival();
            aux.setVisible(true);
        }
        if (puntos.getSelectedIndex() == 2) {
            this.setVisible(false);
            Doblepique aux = new Doblepique();
            aux.setVisible(true);
        }
        if (puntos.getSelectedIndex() == 3) {
            this.setVisible(false);
            Winneer aux = new Winneer();
            aux.setVisible(true);
        }
    }//GEN-LAST:event_puntosActionPerformed

